local function CTL_CODE(DeviceType, Function, Method, Access) 
    return bit.bor( bit.lshift(DeviceType,16), bit.lshift(Access, 14)
        ,bit.lshift(Function,2),Method)
  --(((DeviceType) << 16) | ((Access) << 14) | ((Function) << 2) | (Method)) 
end

